import Empplan from "../assets/clients/client-empplan.jpeg";
import Koo from "../assets/clients/client-koo.jpeg";
import CarouselSlider from "react-carousel-slider";

function Testimonials({ testimonialData }) {

  let sliderBoxStyle = {
    height: "400px",
    width: "600px",
    background: "transparent"
  };

  let itemsStyle = {
    // , margin: "0px 0px", padding: "0px"
  };

  let buttonSetting = {
    placeOn: "bottom-beneath"
  };

  let manner = {
    autoSliding: { interval: "2s" }
  };  

  const element1 = () => (
    <div className="testimonial-item" data-aos="fade-up">
      <p>
        <i className="bx bxs-quote-alt-left quote-icon-left"></i>
        {testimonialData.feedBack}
        <i className="bx bxs-quote-alt-right quote-icon-right"></i>
      </p>
      <img src={Koo} className="testimonial-img" alt="" />
      <h3>Saul Goodman</h3>
      <h4>Ceo &amp; Founder</h4>
    </div>
  );

  const element2 = () => (
    <div
      className="testimonial-item"
      data-aos="fade-up"
      data-aos-delay="100"
    >
      <p>
        <i className="bx bxs-quote-alt-left quote-icon-left"></i>
        {testimonialData.feedBack}
        <i className="bx bxs-quote-alt-right quote-icon-right"></i>
      </p>
      <img src={Empplan} className="testimonial-img" alt="" />
      <h3>Sara Wilsson</h3>
      <h4>Designer</h4>
    </div>
  )

  const components = [element1, element2];

  return (
    <section id="testimonials" className="testimonials section-bg">
      <div className="container">
        <div className="section-title">
          <h2>Testimonials</h2>
          <p>{testimonialData.description}</p>
        </div>

        <div
          className="testimonials-slider swiper"
          data-aos="fade-up"
          data-aos-delay="100"
        >
          <div className="swiper-wrapper">
            <CarouselSlider
              slideCpnts={components}
              manner={manner}
              buttonSetting={buttonSetting}
              sliderBoxStyle={sliderBoxStyle}
              itemsStyle={itemsStyle}
            />
          </div>
          <div className="swiper-pagination"></div>
        </div>
      </div>
    </section>
  );
}

export default Testimonials;
