/* eslint-disable jsx-a11y/iframe-has-title */

import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

/* eslint-disable jsx-a11y/no-redundant-roles */
function Contact({ contactData }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [show, setShow] = useState("");

  const API = "http://localhost:4000";

  const formDetail = {
    subject: "Sending Mail",
    html: `
    <div style="border: 1px solid #dadce0; width: 500px; border-radius :8px; padding: 40px 20px; text-align: center; margin: auto">
    <p style="font-size: 20px">${name}<br /></p> 
    <p style="font-size: 18px">${email}<br /></p> 
    <p style="font-size: 18px">${subject}<br /></p>
    <hr />
    <p style="font-size: 15px; text-align: left">${message}</p></div>
    `,
  };

  const sumbitForm = (e) => {
    e.preventDefault();
    // const id = toast.loading("Please wait...");
    const myPromise = new Promise((resolve) =>
      fetch(`${API}/text_mail`, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formDetail),
      }).then((response) => {
        resolve(response);
      })
    );
    toast.promise(myPromise, {
      pending: "Please wait...",
      success: "Thank you for registration. My team will contact you",
      error: "Something went wrong",
    });
    setName("");
    setEmail("");
    setSubject("");
    setMessage("");
  };

  return (
    <section id="contact" className="contact">
      <div className="container">
        <div className="section-title">
          <h2>Contact</h2>
          <p>{contactData.description}</p>
        </div>

        <div className="row" data-aos="fade-in">
          <div className="col-lg-5 d-flex align-items-stretch">
            <div className="info">
              <div className="address">
                <i className="bi bi-geo-alt"></i>
                <h4>Location:</h4>
                <p>{contactData.location}</p>
              </div>

              <div className="email">
                <i className="bi bi-envelope"></i>
                <h4>Email:</h4>
                <p>{contactData.email}</p>
              </div>

              <div className="phone">
                <i className="bi bi-phone"></i>
                <h4>Call:</h4>
                <p>{contactData.call}</p>
              </div>

              <iframe
                src="https://www.google.com/maps/embed?pb=!1m14!1m8!1m3!1d12097.433213460943!2d-74.0062269!3d40.7101282!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0xb89d1fe6bc499443!2sDowntown+Conference+Center!5e0!3m2!1smk!2sbg!4v1539943755621"
                frameborder="0"
                style={{ border: 0, width: "100", height: "290px" }}
                allowfullscreen
              ></iframe>
            </div>
          </div>

          <div className="col-lg-7 mt-5 mt-lg-0 d-flex align-items-stretch">
            <form
              // action="forms/contact.php"
              // method="post"
              // role="form"
              // className="php-email-form"
              onSubmit={sumbitForm}
            >
              <ToastContainer />
              {/* {show && (
                <div>
                  <div className="my-3">
                    <div className="error-message"></div>
                    <div className="sent-message">{show}</div>
                  </div>
                </div>
              )} */}

              <div className="row">
                <div className="form-group col-md-6">
                  <label for="name">Your Name</label>
                  <input
                    type="text"
                    name="name"
                    className="form-control"
                    id="name"
                    required
                    value={name}
                    onChange={(e) => {
                      setName(e.target.value);
                    }}
                  />
                </div>
                <div className="form-group col-md-6">
                  <label for="name">Your Email</label>
                  <input
                    type="email"
                    className="form-control"
                    name="email"
                    id="email"
                    required
                    value={email}
                    onChange={(e) => {
                      setEmail(e.target.value);
                    }}
                  />
                </div>
              </div>
              <div className="form-group">
                <label for="name">Subject</label>
                <input
                  type="text"
                  className="form-control"
                  name="subject"
                  id="subject"
                  required
                  value={subject}
                  onChange={(e) => {
                    setSubject(e.target.value);
                  }}
                />
              </div>
              <div className="form-group">
                <label for="name">Message</label>
                <textarea
                  className="form-control"
                  name="message"
                  rows="10"
                  required
                  value={message}
                  onChange={(e) => {
                    setMessage(e.target.value);
                  }}
                ></textarea>
              </div>

              <div className="text-center">
                <button type="submit">Send Message</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
